var GroupAssignedUtils = Class.create();
GroupAssignedUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getGroupAssigned: function() {
        var userGroup = new GlideRecord('sys_grp_user');
        userGroup.addQuery('user', this.getParameter(sysparm_userID));
        userGroup.setLimit(1);
        userGroup.query();
        userGroup.next();
        return usergroup.group;

    },
    type: 'GroupAssignedUtils'
});
